import React, { useState } from 'react';
import '../styles/Trial.scss';

const Trial = () => {
  // State for day/night mode
  const [isNight, setIsNight] = useState(false);

  // Function to toggle between day and night mode
  const toggleDayNight = () => {
    setIsNight(prev => !prev);
  };

  return (
    <div className={`Trial ${isNight ? 'dark' : 'light'}`}>
      <div className="grid">
        <div className="card">
          <div className="shine"></div>
          <div className="icon">
            <svg viewBox="0 0 24 24">
              {/* Your SVG icon */}
              <path d="M12 2a9 9 0 00-9 9v7h1.29l.67 2.4A1.3 1.3 0 006 22h12a1.3 1.3 0 001.04-1.6L19.29 18H21v-7a9 9 0 00-9-9zM4 11a8 8 0 0116 0v7h-1.94l-1.11 4H6.05L4.94 18H4v-7zm4 2h8v1H8v-1zm0 3h6v1H8v-1z" />
            </svg>
          </div>
          <h4>Sample Card</h4>
          <p>This is a card description.</p>
        </div>

        <div className="card">
          <div className="shine"></div>
          <div className="icon">
            <svg viewBox="0 0 24 24">
              <path d="M12 2a9 9 0 00-9 9v7h1.29l.67 2.4A1.3 1.3 0 006 22h12a1.3 1.3 0 001.04-1.6L19.29 18H21v-7a9 9 0 00-9-9zM4 11a8 8 0 0116 0v7h-1.94l-1.11 4H6.05L4.94 18H4v-7zm4 2h8v1H8v-1zm0 3h6v1H8v-1z" />
            </svg>
          </div>
          <h4>Another Card</h4>
          <p>Another card description.</p>
        </div>
      </div>

      {/* Day/Night Toggle */}
      <div className="day-night">
        <label>
          <input type="checkbox" checked={isNight} onChange={toggleDayNight} />
          <div></div>
        </label>
      </div>

      {/* Twitter Icon */}
      <div className="twitter">
        <a href="https://twitter.com">
          <svg viewBox="0 0 24 24">
            <path d="M22.46 6c-.77.35-1.6.6-2.46.7a4.14 4.14 0 001.84-2.28 8.13 8.13 0 01-2.6.99A4.14 4.14 0 0016.3 4a4.16 4.16 0 00-4.13 4.17c0 .32.03.63.1.93A11.8 11.8 0 013 5.57c-.35.62-.55 1.34-.55 2.1 0 1.44.73 2.71 1.85 3.45-.68-.02-1.32-.21-1.88-.52v.05a4.17 4.17 0 003.34 4.08c-.32.09-.67.13-1.02.13-.25 0-.5-.02-.73-.07a4.15 4.15 0 003.88 2.89 8.34 8.34 0 01-5.15 1.78c-.33 0-.66-.02-.99-.05A11.78 11.78 0 006.29 21c7.6 0 11.75-6.3 11.75-11.75 0-.18-.01-.36-.02-.54.8-.59 1.49-1.33 2.04-2.17z" />
          </svg>
        </a>
      </div>
    </div>
  );
};

export default Trial;
