import React, { useState } from 'react';
import '../styles/services.scss'; // Ensure you have this SCSS file for styling

const servicesData = {
  "Data Management": [
    { title: "Manual Data Entry", icon: "📋" },
    { title: "Automated Data Entry", icon: "⚙️" },
    { title: "Data Validation & Management", icon: "✅" },
  ],
  "Customer Support": [
    { title: "Customer Service Support", icon: "📞" },
    { title: "Telemarketing & Sales Support", icon: "📈" },
    { title: "Technical Support & Helpdesk Services", icon: "🛠️" },
  ],
  "Financial Services": [
    { title: "Bookkeeping", icon: "📊" },
    { title: "Financial Reporting", icon: "📝" },
    { title: "Payroll Management", icon: "💵" },
    { title: "Accounts Receivable & Payable", icon: "📥" },
  ],
  "Business Development": [
    { title: "Roles & Expertise", icon: "👔" },
    { title: "Strategy Development", icon: "📈" },
    { title: "Execution", icon: "🚀" },
  ],
  "Legal Services": [
    { title: "Document Preparation & Review", icon: "📄" },
    { title: "Contract Management", icon: "📝" },
    { title: "Legal Research & Compliance", icon: "🔍" },
  ],
  "Technical Services": [
    { title: "IT Support & Infrastructure Management", icon: "💻" },
    { title: "Network Setup & Maintenance", icon: "🌐" },
    { title: "Custom Application Design & Development", icon: "🖥️" },
  ],
};

const Services = () => {
  const [activeTab, setActiveTab] = useState("Data Management");

  return (
    <div className="services-container">
      <h1>What We Do?</h1>
      <div className="tabs">
        {Object.keys(servicesData).map((tab) => (
          <button
            key={tab}
            className={`tab ${activeTab === tab ? 'active' : ''}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>
      <div className="service-details">
        {servicesData[activeTab].map((service) => (
          <div className="service-item" key={service.title}>
            <span className="service-icon">{service.icon}</span>
            <h4>{service.title}</h4>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Services;
